<html xmlns="http://www.w3.org/1999/xhtml" > <head runat="server"> <title>Untitled Page</title> <script runat="server"> protected void Page_Load(object sender, EventArgs e) { System.Data.DataTable table = new System.Data.DataTable(); table.Columns.Add("num");
System.Data.DataRow row = table.NewRow(); row["num"] = (double)12.56; table.Rows.Add(row);
row = table.NewRow(); row["num"] = (int)12; table.Rows.Add(row);
row = table.NewRow(); row["num"] = DBNull.Value; table.Rows.Add(row);
row = table.NewRow(); row["num"] = "33"; table.Rows.Add(row);
row = table.NewRow(); table.Rows.Add(row);
Repeater1.DataSource = table; Repeater1.DataBind(); } </script> </head> <body> <form id="form1" runat="server"> <asp:Repeater runat="server" ID="Repeater1"> <ItemTemplate> <radI:RadNumericTextBox DbValue='<%# Bind("num") %>' ID="RadNumericTextBox1" runat="server"> </radI:RadNumericTextBox> </ItemTemplate> </asp:Repeater> </form> </body> </html>
